(function(){tinymce.create('tinymce.plugins.HumpyardLinkPlugin',{init:function(ed,url){this.editor=ed;ed.addCommand('mceHumpyardLink',function(){var se=ed.selection;if(se.isCollapsed()&&!ed.dom.getParent(se.getNode(),'A'))return;ed.windowManager.open({file:(hyAdminPath?'/'+hyAdminPath:'')+'/tiny_mce/link',width:480+parseInt(ed.getLang('hylink.delta_width',0)),height:400+parseInt(ed.getLang('hylink.delta_height',0)),inline:1},{plugin_url:url})});ed.addButton('link',{title:'hylink.link_desc',cmd:'mceHumpyardLink'});ed.addShortcut('ctrl+k','hylink.hylink_desc','mceHumpyardLink');ed.onNodeChange.add(function(ed,cm,n,co){cm.setDisabled('link',co&&n.nodeName!='A');cm.setActive('link',n.nodeName=='A'&&!n.name)})},getInfo:function(){return{longname:'Humpyard link',author:'Sven G. Br√∂nstrup',authorurl:'http://humpyard.org',infourl:'http://humpyard.org',version:tinymce.majorVersion+"."+tinymce.minorVersion}}});tinymce.PluginManager.add('hylink',tinymce.plugins.HumpyardLinkPlugin)})();